package assignments_B45;

import java.util.Scanner;

public class Ass1_Add_sub_Mul_Div {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=80;
		int b=4;
		
		int c=a+b;
		int d=a-b;
		int e=a*b;
		int f=a/b;
		int g=a%b;
		
		System.out.println("Addition Output "+c);
		System.out.println("Subtraction Output "+d);
		System.out.println("Multiplication Output "+e);
		System.out.println("Division Output "+f);
		System.out.println("Modulus Output "+g);
		
	}

}
