package assignments_B45;
//52.Print  0 to 10
//53.Print 10 to 0
//54.Print -5 to +5
public class Ass_b45_52_53_54_Print_numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=1;i<=10;i++)
		{
			System.out.println("Print 1 to 10 numbers - " + i );
		}
		for(int i=10;i>0;i--)
		{
			System.out.println("Print 10 to 1 numbers - " + i );
		}
		for(int i=-5;i<=5;i++)
		{
			System.out.println("Print -5 to +5 numbers - " + i );
		}
	}

}
